mapper.select:
select
    aa.*
from
    ap_article aa
    inner join ap_article_config as
    on ac.`article_id` = aa.`id`

    AND aa.`channel_id` = #{dto.tag}

and ac.`is_down` =0
and ac.`is_delete` =0

    AND aa.`publish_time` < #{dto.minBehotTime}


AND aa.`publish_time` > #{dto.maxBehotTime}

order by aa.`publish_time` desc
LIMIT #{dto.size}
